% humans can hear from 20 Hz to 20 kHzfreq = 44.1e3;t = 0:1/freq:4;f= logspace(1.3, 4.3);w = 2*pi*f;% log of 20 is close to 1.3, log of 20k is close to 4.3responselow = zeros(size(f));responsehigh = zeros(size(f));% for every frequency in the logspace, create the input function and run it% through a highpass and lowpass filter. Take the end time input to output% ratio, as at the end the system should be stable.for x=1:length(f)    in = exp(1i*w(x)*t);    Hlow = lsim(1/R/C, [1, 1/R/C], in, t);    Hhigh = lsim([1, 0],[1, 1/R/C], in, t);    responselow(x) = Hlow(end) / in(end);    responsehigh(x) = Hhigh(end) / in(end);end% Plot using the required log scale etcfigure;subplot(2, 1, 1);semilogx(f, 20*log(abs(responselow)));title("|H(w)| Lowpass");xlabel("Frequency");ylabel("Db");subplot(2, 1, 2);semilogx(f, angle(responselow)/pi);title("H(w) Angle");xlabel("Frequency");ylabel("Angle");